$countryKeys = array_map(function ($country) {
	return Tools::strtoupper($country['alpha2Code']);
}, $countries);

$langIso = Tools::strtolower(Context::getContext()->language->iso_code);
if (!in_array($langIso, array_keys($countries[0]['translations']))) {
	$langIso = '';
}

if ($langIso) {
	$countryValues = array_map(function ($country) use ($langIso) {
		return $country['translations'][$langIso];
	}, $countries);
} else {
	$countryValues = array_map(function ($country) {
		return $country['name'];
	}, $countries);
}

$countries = [];
foreach ($countryKeys as $index => $countryKey) {
	$countries[] = [
		'id' => $countryKey,
		'name' => $countryValues[$index],
	];
}

return $countries;
